package net.carshopping.services.manage.orderdetail.impl;import java.util.List;import net.carshopping.core.ServersManager;import net.carshopping.services.manage.order.bean.Order;import net.carshopping.services.manage.orderdetail.OrderdetailService;import net.carshopping.services.manage.orderdetail.bean.Orderdetail;import net.carshopping.services.manage.orderdetail.dao.OrderdetailDao;import net.carshopping.web.action.manage.report.ReportInfo;public class OrderdetailServiceImpl extends ServersManager<Orderdetail>		implements OrderdetailService {	private OrderdetailDao orderdetailDao;	public void setOrderdetailDao(OrderdetailDao orderdetailDao) {		this.orderdetailDao = orderdetailDao;	}	@Override	public List<ReportInfo> reportProductSales(Orderdetail orderdetail) {		return orderdetailDao.reportProductSales(orderdetail);	}}